#!/bin/sh
tshark -r not_common.pcap -Y "" -T fields -e ip.src -e dns.qry.name | gawk '{ip = $1; domain = $2; if(last[ip] != domain) {last[ip] = domain; split(domain, a, "."); tld = a[length(a)]; count[ip, tld] += 1; count_2[ip] += 1}} END {check["com"] = 1; check["in"] = 1; check["pw"] = 1; check["ru"] = 1; printf "ip\t"; for(tld in check) printf "#%s\t", tld; printf "#other\t||\t"; for(tld in check) printf "%s_%.6lf\t", tld, check[tld]; printf "||\n"; for(ip in last){ s = 0; entropy = 0; display = 1; for(tld in check) if(! (ip, tld) in count) display = 0; else s += count[ip, tld]; if(display == 0) continue; printf "%s\t", ip; for(tld in check) printf "%d\t", count[ip, tld]; printf "%d\t\t", count_2[ip] - s; for(tld in check) {prob = count[ip, tld] / s; printf "%.10lf\t", prob;} printf "\n"}}'

tshark -r not_common.pcap -Y "" -T fields -e ip.src -e dns.qry.name | gawk '{ip = $1; domain = $2; if(last[ip] != domain) {last[ip] = domain; split(domain, a, "."); tld = a[length(a)]; count[ip, tld] += 1; count_2[ip] += 1}} END {check["ru"] = 1; check["org"] = 1; check["info"] = 1; check["biz"] = 1; check["net"] = 1; printf "ip\t"; for(tld in check) printf "#%s\t", tld; printf "#other\t||\t"; for(tld in check) printf "%s_%.6lf\t", tld, check[tld]; printf "||\n"; for(ip in last){ s = 0; entropy = 0; display = 1; for(tld in check) if(! (ip, tld) in count) display = 0; else s += count[ip, tld]; if(display == 0) continue; printf "%s\t", ip; for(tld in check) printf "%d\t", count[ip, tld]; printf "%d\t\t", count_2[ip] - s; for(tld in check) {prob = count[ip, tld] / s; printf "%.10lf\t", prob;} printf "\n"}}'
